import React from 'react'
import Die from './components/Die'
import { nanoid } from 'nanoid'


export default function App() {
    const [dice, setAllDice] = React.useState(allNewDice())
    const [tenzies, setTenzies] = React.useState(false)

    function checkCondition() {
        let result = false
        for (let die of dice) {
            if (die.isHeld) {
                result = true
            } else {
               return false
            }
        }
        for (let i = 0; i < dice.length - 1; i++) {
            if (dice[i].value === dice[i + 1].value) {
                result = true
            } else {
                return false
            }
        }
        return result
    }



    React.useEffect(() => {
        const allHeld = dice.every()
        setTenzies(checkCondition())
    }, [dice])
    tenzies && console.log(`You Win`)


    function generateNewDie() {
        return {
            value: Math.floor(Math.random() * 6 + 1),
            isHeld: false,
            id: nanoid()
        }
    }

    function allNewDice() {
        let arr = []
        for (let i = 0; i < 10; ++i){
            arr.push(generateNewDie())
        }
        return arr
    }

    function rollDice() {
        setAllDice(prevDice => (
            prevDice.map(die => (die.isHeld ? die : generateNewDie()))
        ))
    }

    function holdDice(id) {
        setAllDice(prevDice => (prevDice.map(die => ({
            ...die,
            isHeld: die.id === id ? !die.isHeld : die.isHeld
        }))))
    }

    const diceElements = dice.map(die => <Die
        isHeld={die.isHeld}
        value={die.value}
        // id={die.id}
        key={die.id}
        holdDice={() => holdDice(die.id)}
        />)


    return (
        <main>
            <h1 className="title">Tenzies</h1>
            <p className="instructions">Roll until all dice are the same.
                Click each die to freeze it at its current value between rolls.
            </p>
            <div className="container">
                {diceElements}
            </div>
            <div className='roll-dice'>
                <button onClick={rollDice}>Roll</button>
            </div>
        </main>
    )
}
